/* Image Resizing */
template<typename Valtype>
IPPI_WRAP ippiResizeGetSize(IppiSize srcSize, IppiSize dstSize, IppiInterpolationType interpolation, Ipp32u antialiasing, Ipp32s* pSpecSize, Ipp32s* pInitBufSize);
