/* //////////////////////////////////////////////////////////////////////////// 
//  Name:       ippiConvert
//
//  Purpose:    Converts pixel values of an image from one bit depth to another
//
//  Returns:
//    ippStsNullPtrErr      One of the pointers is NULL
//    ippStsSizeErr         roiSize has a field with zero or negative value
//    ippStsStepErr         srcStep or dstStep has zero or negative value
//    ippStsNoErr           OK
//
//  Parameters:
//    pSrc                  Pointer  to the source image
//    srcStep               Step through the source image
//    pDst                  Pointer to the  destination image
//    dstStep               Step in bytes through the destination image
//    roiSize               Size of the ROI
//    roundMode             Rounding mode, ippRndZero or ippRndNear
*/ 
 

 template< typename T > 
IPPI_WRAP ippiConvert_C1R( const T* pSrc, int srcStep, T* pDst, 
                             int dstStep, IppiSize roiSize) { 
	 return IPPCALL(ippiCopy_C1R)(pSrc,srcStep,pDst,dstStep,roiSize);
     } 
 template< typename T > 
IPPI_WRAP ippiConvert_C3R( const T* pSrc, int srcStep, T* pDst, 
                             int dstStep, IppiSize roiSize) { 
	 return IPPCALL(ippiCopy_C3R)(pSrc,srcStep,pDst,dstStep,roiSize);
     } 
 template< typename T > 
IPPI_WRAP ippiConvert_AC4R( const T* pSrc, int srcStep, T* pDst, 
                             int dstStep, IppiSize roiSize) { 
	 return IPPCALL(ippiCopy_AC4R)(pSrc,srcStep,pDst,dstStep,roiSize); 
     } 


